import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import { Redirect } from 'react-router';

import * as placesActions from '../../store/actions/index';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';
import Container from '@material-ui/core/Container';
import BackdropUI from '../UI/Backdrop/BackdropUI';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import { useStyles } from '../Styles/Styles';

const PlaceView = props => {

    // styles
    const classes = useStyles();

    //dependecy
    // const { onGetAllPlaces } = props;

    useEffect(() => {
        if (props.token !== null) {
			//TODO: Get place info
        }
    }, [onGetAllPlaces, props.history, props.token])


    if (!props.loading) {
        places = props.places.map(place => {
            return <Place
                key={place.ID}
                id={place.ID}
                name={place.name}
                persons={place.people}
            />
        });
    }

    if (places.length === 0) {
        places = (<h3>Not found!</h3>);
    }

    if (props.error) {
        places = (
            <BackdropUI>
                <DialogContent>
                    <DialogContentText id="alert-dialog-description">
                        {props.error}
                    </DialogContentText>
                </DialogContent>
            </BackdropUI>
        );
    }

    // is authenticated
    let redirect = !props.token ? <Redirect to='/' /> : null;

    return (
		<div>
			{redirect}

			<Container maxWidth="md" >
			<Grid>
				<Box boxShadow={3} style={{ padding: '50px' }}>


					<form onSubmit={onSubmitHandler} noValidate style={{ inline: true }}>
						<div>
							<Button type="submit" variant="contained" color="primary">-</Button>
						</div>
					</form>

					<h1><bold> 50</bold></h1>

					<form onSubmit={onSubmitHandler} noValidate style={{ inline: true }}>
						<div>
							<Button type="submit" variant="contained" color="primary">+</Button>
						</div>
					</form>

				</Box>
			</Grid>
			</Container >

        </div>
    );

}

// get state from reducer
const mapStateToProps = (state) => {
    return {
        token: state.auth.token,
        places: state.places.places,
        loading: state.loadingError.loading,
        error: state.loadingError.error,
    };
}

// actions to reducer (dispatch)
const mapDispatchToProps = (dispatch) => {
    return {
        onGetAllPlaces: (token) => dispatch(placesActions.fetchAllPlaces(token)),
    };
}

export default connect(mapStateToProps, mapDispatchToProps)(Places);
